INSERT INTO `book` (`id`,`id_topic`,`id_origin`,`title`,`authors`,`collection`,`collection_number`,`city`,`country`,`keywords`,`edition`,`editorial`,`description`,`summary`,`publish_date`,`year`,`size`,`isbn`,`issn`,`price_sus`,`number_of_pages`,`cover`,`featured`,`is_new`,`creation_date`,`last_modified`) VALUES (32,3,32,'<El> labio inferior','Gomez, Sergio','Biblioteca Breve / Seix Barral',NULL,'Santiago de Chile','CL','NOVELAS CHILENAS; LITERATURA CHILENA; LITERATURA','1 ed','Editorial Planeta Chilena; Grupo Editorial Planeta',NULL,'En "El Labio Inferior" reencontramos los lugares miticos que recorren toda la narrativa de Sergio Gomez. Ahora se trata de Vertiente Baquedano, un pueblo que esta a no muchos kilometros del ya familiar Parque Deportivo. Un medico ha desaparecido y su testimonio es indispensable para esclarecer el misterioso homicidio de varios antiguos integrantes del club local de futbol','julio 1998',NULL,'21 x 13 cm','956-247-205-1',NULL,15,287,NULL,NULL,NULL,NULL,NULL);
INSERT INTO `book` (`id`,`id_topic`,`id_origin`,`title`,`authors`,`collection`,`collection_number`,`city`,`country`,`keywords`,`edition`,`editorial`,`description`,`summary`,`publish_date`,`year`,`size`,`isbn`,`issn`,`price_sus`,`number_of_pages`,`cover`,`featured`,`is_new`,`creation_date`,`last_modified`) VALUES (33,12,33,'Nueva capital para Chile. La alternativa al holocausto de Santiago para despues del 2000','Elton, Elias',NULL,NULL,'Santiago','CL','URBANISMO; DEMOGRAFIA; CRECIMIENTO URBANO; POBREZA URBANA; SOCIOLOGIA; DESARROLLO URBANO; ARQUITECTU','1 ed','Editorial Reinventando Chile','Ilustraciones','1. Una carta al diario que no fue publicada. 2. El estado, las ciudades y la pobreza: una invitacion a crear. 3. Ventajas de la nueva ciudad. 4. Grandes proyectos operativos. 5. Arquitectura: equilibrio y trascendencia. 6. Programas de accion. 7. Magnitud de la nueva ciudad. 8. Sociologia y marketing','agosto 1998',NULL,'22 x 15 cm','956-7483',NULL,20,127,NULL,NULL,NULL,NULL,NULL);
INSERT INTO `book` (`id`,`id_topic`,`id_origin`,`title`,`authors`,`collection`,`collection_number`,`city`,`country`,`keywords`,`edition`,`editorial`,`description`,`summary`,`publish_date`,`year`,`size`,`isbn`,`issn`,`price_sus`,`number_of_pages`,`cover`,`featured`,`is_new`,`creation_date`,`last_modified`) VALUES (34,13,34,'Pedro de Valdivia. Una historia salitrera','Emparan V., Benjamin','Ediciones Universitarias. Vol. 44',NULL,'Antofagasta','CL','HISTORIA; EXPLOTACION DE SALITRE; CRECIMIENTO URBANO; DESARROLLO URBANO; HISTORIA REGIONAL','1 ed','Universidad Catolica del Norte. Ediciones Universitarias','Fotos','Pedro no se fue con la camanchaca. La oficina salitrera de Pedro de Valdivia. Forjadores de la oficina. Cultos religiosos pedrinos. Solo el recuerdo de la escuela "Luis Cruz Martinez". Fiesta pampina. La tarjetera. Correso de Chile. Los almacenes de la pampa. El registro civil pedrino. Carabineros de Chile en Padro de Valdivia. Y fueron comerciantes. Ranchos y restaurantes pedrinos. Soledad. El transporte de pasajeros en la pampa. Agrupaciones pampinas. Las calles de las viandas en Pedro de Valdivia. Botaderos del desierto. Apuntes sobre la carreta. Debe y haber de una Salitrera Shanks. Una feria de antiguedades','octubre 1997',NULL,'25 x 17 cm','956-7012-70-9',NULL,26,87,NULL,NULL,NULL,NULL,NULL);
