INSERT INTO `book` (`id`,`id_topic`,`id_origin`,`title`,`authors`,`collection`,`collection_number`,`city`,`country`,`keywords`,`edition`,`editorial`,`description`,`summary`,`publish_date`,`year`,`size`,`isbn`,`issn`,`price_sus`,`number_of_pages`,`cover`,`featured`,`is_new`,`creation_date`,`last_modified`) VALUES (37,5,37,'<Los> movimientos sociales en Chile, 1973-1993','Guillaudat, Patrick; Mouterde, Pierre','Coleccion Sin Norte',NULL,'Santiago de Chile','CL','HISTORIA POLITICA; SOCIOLOGIA; CONVULSION SOCIAL; DICTADURA MILITAR; SINDICALISMO CHILENO; HISTORIA ',NULL,'Lom Ediciones',NULL,'Chile ¿un pais modelo?. Comprender a Chile. Un repaso a la historia. El tiempo de la radicalizacion y los efectos de la revolucion cubana. El golpe de estado. En nombre de la seguridad nacional. Aliados decisivos. La mano de los Estados Unidos. Politicas economicas de clase. La implacable represion. Entre el heroismo y el mimetismo. La personalizacion del regimen. La institucionalizacion de las relaciones laborales. Una constitucion hecha a la medida. El lento ascenso de los opositores. El imperio de los financistas. La gran crisis. El 11 de mayo de 1983. La "bidonviloizacion" de las clases populares. Primer acto (1983-1984): el retorno de los partidos. Acto 2: los años decisivos (1984-1986). Acto 3: en la ruta trazada por la dictadura (1987-1988). Acto 4: el desenlace (1988-1989). Una transicion muy controlada. Los primeros pasos. Los conflictos en ascenso. Las politicas economicas: ¿libres de toda sospecha?. La sucesion de Aylwin','1998',NULL,'22 x 16 cm','956-282-097-1',NULL,15,248,NULL,NULL,NULL,NULL,NULL);
